import React from "react";
import adminLayout from "../hoc/adminLayout";
import axios from "axios";
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Modal';

class UsersPage extends React.Component {
    constructor(props)
    {
        super(props);
        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
        this.editUser=this.editUser.bind(this);
        this.deleteUser=this.deleteUser.bind(this);
        this.state = 
        {
            users:[],
            id: '',
            showModal: false,
        }
    }
    handleOpenModal (e) {
        e.preventDefault();
        this.setState({ showModal: true });
    }

    handleCloseModal (e) {
        e.preventDefault();
        this.setState({ showModal: false });
        console.log();
        }

    editUser(e,id)
    {
        e.preventDefault();
        axios.post('http://localhost/project9/admin/PHP/users.php?id='+id);
    }
    deleteUser(e,id)
    {
        e.preventDefault();
        axios.post('http://localhost/project9/admin/PHP/deleteuser.php?id='+id)
    }
    componentDidMount()
    {
        axios.get('http://localhost/project9/admin/PHP/users.php')
        .then((res)=>{
          this.setState({users: res.data});
          console.log(this.state.users);
        })
    }
    componentDidUpdate(){
        axios.get('http://localhost/project9/admin/PHP/users.php')
        .then((res)=>{
          this.setState({users: res.data});
          console.log(this.state.users);
        })
    }

    render(){
        return <>
                        <div className="table-container">
                <div className="row">
                    <div className="col">
                        <h5 className="pb-2 mb-0">Table</h5>
                    </div>
                    <div className="col text-right">
                        <button className="btn btn-default low-height-btn" onClick={(e)=>{this.handleOpenModal(e)}}>
                            <i className="fa fa-plus"></i>
                        </button>
                        <Modal show={this.showModal} onHide={this.handleCloseModal} animation={false}>
                            <Modal.Header closeButton>
                            <Modal.Title>Modal heading</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                            <Modal.Footer>
                            <Button variant="secondary" onClick={(e)=>{this.handleCloseModal(e)}}>
                                Close
                            </Button>
                            <Button variant="primary" onClick={(e)=>{this.handleCloseModal(e)}}>
                                Save Changes
                            </Button>
                            </Modal.Footer>
                        </Modal>
                    </div>
                </div>
                <p>
                    -----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
                </p>
                <div className="d-flex text-muted">
                    <table className="table">
                        <thead>
                            <tr>
                                {/* <th><input type="checkbox"/></th> */}
                                <th>ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>city</th>
                                <th>Phone Number</th>
                                <th>Created On</th>
                                <th>Updated On</th>
                                <th>status</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.users && this.state.users.map(user =>
                            {return(
                                    <tr>
                                    <td>{user.id}</td>
                                    <input type="hidden" onChange={(e)=>{this.setState({id: e.target.value})}} value={user.id}/>
                                    <td>{user.username}</td>
                                    <td>{user.email}</td>
                                    <td>{user.city}</td>
                                    <td>{user.phone_number}</td>
                                    <td>{user.created_at}</td>
                                    <td>{user.updated_at}</td>
                                    <td>{user.deleted == 1? 'Deleted':'Active'}</td>
                                    <td></td>
                                    <td>
                                        <div className="dropdown table-action-dropdown">
                                            <button className="btn btn-secondary btn-sm dropdown-toggle" type="button" id="dropdownMenuButtonSM" data-bs-toggle="dropdown" aria-expanded="false"><i className="fa fa-ellipsis-v" aria-hidden="true"></i></button>
                                            <ul className="dropdown-menu" aria-labelledby="dropdownMenuButtonSM">
                                                <li><button className="dropdown-item" onClick={(e)=>{this.editUser(e,user.id)}}><i className="fa fa-pencil" aria-hidden="true"></i>&nbsp;Edit</button></li>
                                                <div className="dropdown-divider"></div>
                                                <li><button className="dropdown-item text-danger" onClick={(e)=>{this.deleteUser(e,user.id)}}><i className="fa fa-trash" aria-hidden="true"></i>&nbsp;Delete</button></li>
                                            </ul>
                                        </div>
                                    </td>
                                </tr>
                            )})}

                        </tbody>
                    </table>
                </div>
                <nav className="table-bottom-center-pagination" aria-label="Page navigation example ">
                    <ul className="pagination">
                        <li className="page-item">
                        <a className="page-link" href="#" aria-label="Previous">
                            <span aria-hidden="true">&laquo;</span>
                            <span className="sr-only">Previous</span>
                        </a>
                        </li>
                        <li className="page-item"><a className="page-link" href="#">1</a></li>
                        <li className="page-item"><a className="page-link" href="#">2</a></li>
                        <li className="page-item"><a className="page-link" href="#">3</a></li>
                        <li className="page-item">
                        <a className="page-link" href="#" aria-label="Next">
                            <span aria-hidden="true">&raquo;</span>
                            <span className="sr-only">Next</span>
                        </a>
                        </li>
                    </ul>
                </nav>
            </div>
        </>
    }
}

export default adminLayout(UsersPage);
