import React,{useState} from "react";
import "../../assets/css/login.css"
import { Link } from 'react-router-dom';
import authLayout from "../../hoc/authLayout";

function LoginPage() {

    const [email, setEmail]=useState('');
    const [emailError, setEmailErorr]=useState('');
    const [password, setPassword]=useState('');
    const [passwordError, setPasswordError]=useState('');

    const handleEmailChange=(e)=>{
      setEmailErorr('');
      setEmail(e.target.value);
    }

    const handlePasswordChange=(e)=>{
      setPasswordError('');
      setPassword(e.target.value);
    }
    const handleFormSubmit=(e)=>{
      e.preventDefault();

      if (email!=='')
      {
          if (email==='admin@admin.com') 
          {
            setEmailErorr('');
            if (password!=='')
            {

              if (password==='password') 
              {
                window.location.href='/dashboard';
              }
              else
              {
                setPasswordError('Invalid credintals');
              }
            }
            else
            {
              setPasswordError('Password Required');
            }
          }
          else
          {
            setEmailErorr('Invalid credintals')
          }
      }
      else
      {
        setEmailErorr('Email Required');
      }


}
  return (
    <>
            <form className="login-form">
                <div className="d-flex align-items-center my-4">
                    <h1 className="text-center fw-normal mb-0 me-3">Sign In</h1>
                </div>
                {/* <!-- Email input --> */}
                <div className="form-outline mb-4">
                    <label className="form-label" htmlFor="form3Example3">Email address</label>
                    <input type="email" id="form3Example3" className="form-control form-control-lg"
                    placeholder="Enter a valid email address" onChange={handleEmailChange} value={email}/>
                </div>
                <div className="d-flex justify-content-between align-items-center">
                    <div className="form-check mb-0">
                    {passwordError&&<div className='error-msg'>{passwordError}</div>}       dd
                    </div>
                </div>

                {/* <!-- Password input --> */}
                <div className="form-outline mb-3">
                    <label className="form-label" htmlFor="form3Example4">Password</label>
                    <input type="password" id="form3Example4" className="form-control form-control-lg"
                    placeholder="Enter password" onChange={handlePasswordChange} value={password}/>
                </div>

                <div className="d-flex justify-content-between align-items-center">
                    {/* <!-- Error Message --> */}
                    <div className="form-check mb-0">
                    {passwordError&&<div className='error-msg'>{passwordError}</div>}       dd
                    </div>
                </div>

                <div className="text-center text-lg-start mt-4 pt-2">
                    <button type="button" className="btn btn-primary btn-lg" onClick={(e)=>{login(e)}}>Login</button>
                </div>
            </form>
        </>
  )
}

export default LoginPage;
